mapper.update:
UPDATE ${tableName} SET question_text = #{question_text}, option_1 = #{option_1}, option_2 = #{option_2}, option_3 = #{option_3}, option_4 = #{option_4}, correct_answer = #{correct_answer},
image_data = #{image_data, jdbcType=BLOB}
image_data = NULL WHERE id = #{id}

UPDATE users SET ISCHATBANNED = 1, BANNED_TIMESTAMP = #{bannedTimestamp} WHERE user_no IN
#{userNo}
AND ISCHATBANNED = 0

mapper.select:
	SELECT ach_title, ach_content, ach_reward, ach_type
	FROM (
    	SELECT
        	ach_title, ach_content, ach_reward, ach_type,
        	ROWNUM rn
    	FROM (
        	SELECT ach_title, ach_content, ach_reward, ach_type
        	FROM ACHIEVEMENTS


                	ach_type = #{type, jdbcType=VARCHAR} 

                	AND (ach_title LIKE '%' || #{query, jdbcType=VARCHAR} || '%' OR ach_content LIKE '%' || #{query, jdbcType=VARCHAR} || '%') 

        	ORDER BY ach_title ASC
    	)
    	WHERE ROWNUM <= #{offset, jdbcType=NUMERIC} + #{limit, jdbcType=NUMERIC}
	)
	WHERE rn > #{offset, jdbcType=NUMERIC}

SELECT COUNT(*) FROM ACHIEVEMENTS
ach_type = #{type}
AND (ach_title LIKE '%' || #{query} || '%' OR ach_content LIKE '%' || #{query} || '%')

mapper.delete:
DELETE FROM ACHIEVEMENTS


        ach_type = #{type}


        AND ach_title IN

            #{title}
