mapper.select:

SELECT * FROM (
	SELECT ROWNUM rn, t.* FROM (
		SELECT i.id,
			u.user_nick AS userNick, i.subject,
			i.message,
			i.created_at AS createdAt
		FROM inquiry i
		JOIN users u ON i.user_id = u.user_id ORDER BY i.created_at DESC
	) t
	WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rn > #{offset}

		SELECT COUNT(*)
		FROM inquiry
        WHERE id = #{id} AND post_password = #{postPassword}
